import React, { Component } from 'react';
import './App.css';

class App extends Component {
  constructor(){
    super();


    this.state= {
      monsters: [
        {
          name: 'Axel',
          id: 'monst1'
        },
        {
          name: 'Élie',
          id: 'monst2'
        },
        {
          name: 'Sylvain',
          id: 'monst3'
        }
      ]

    }

  } ;





  render(){
  return (
    <div className="App">
      {
        this.state.monsters.map( monster => <h1 key={monster.id}>{monster.name}</h1>)
      }
    </div>
  );
}}

export default App;
